import React, { useState } from "react";
import ChatBox from "../components/ChatBox";
import PropertyCard from "../components/PropertyCard";
import axios from "axios";
import "./ChatPage.css";

const ChatPage = () => {
  const [messages, setMessages] = useState([
    {
      sender: "bot",
      text: "Hi ðŸ‘‹! I can help you find properties. Try: '3BHK flat in Pune under 1Cr'",
    },
  ]);
  const [input, setInput] = useState("");
  const [properties, setProperties] = useState([]);

  const sendMessage = async () => {
    if (!input.trim()) return;

    const newMessages = [...messages, { sender: "user", text: input }];
    setMessages(newMessages);
    setInput("");
    setProperties([]); // clear old results

    try {
      const res = await axios.post(
        `${process.env.REACT_APP_API_BASE_URL || "http://localhost:5000"}/api/chat`,
        { query: input }
      );

      const botData = res.data.summary || [];
      if (Array.isArray(botData) && botData.length > 0) {
        // If summaries are returned
        const summaries = botData.map((item) => item.summary);
        const cards = botData.map((item) => item.property);

        setMessages([
          ...newMessages,
          { sender: "bot", text: summaries.join("\n\n") },
        ]);
        setProperties(cards);
      } else {
        setMessages([
          ...newMessages,
          { sender: "bot", text: "No matching properties found." },
        ]);
      }
    } catch (err) {
      console.error(err);
      setMessages([
        ...newMessages,
        { sender: "bot", text: "Error connecting to backend ðŸ˜¢" },
      ]);
    }
  };

  return (
    <div className="chat-page">
      <ChatBox messages={messages} />

      {/* Property Results */}
      {properties.length > 0 && (
        <div className="property-results">
          {properties.map((prop, idx) => (
            <PropertyCard key={idx} property={prop} />
          ))}
        </div>
      )}

      {/* Input Section */}
      <div className="input-section">
        <input
          type="text"
          value={input}
          onChange={(e) => setInput(e.target.value)}
          placeholder="Type your query..."
        />
        <button onClick={sendMessage}>Send</button>
      </div>
    </div>
  );
};

export default ChatPage;
