import React, { useState } from "react";
import ChatBox from "../components/ChatBox";
import axios from "axios";

const ChatPage = () => {
  const [messages, setMessages] = useState([
    { sender: "bot", text: "Hi ðŸ‘‹! I can help you find properties. Try: '3BHK flat in Pune under 1Cr'" },
  ]);
  const [input, setInput] = useState("");

  const sendMessage = async () => {
    if (!input.trim()) return;

    const newMessages = [...messages, { sender: "user", text: input }];
    setMessages(newMessages);
    setInput("");

    try {
      const res = await axios.post(
        `${process.env.REACT_APP_API_BASE_URL || "http://localhost:5000"}/api/chat`,
        { query: input }
      );

      const botReply = res.data.summary || "No matching properties found.";
      setMessages([...newMessages, { sender: "bot", text: botReply }]);
    } catch (err) {
      console.error(err);
      setMessages([...newMessages, { sender: "bot", text: "Error connecting to backend ðŸ˜¢" }]);
    }
  };

  return (
    <div>
      <ChatBox messages={messages} />
      <div style={{ marginTop: "15px" }}>
        <input
          type="text"
          value={input}
          onChange={(e) => setInput(e.target.value)}
          placeholder="Type your query..."
          style={{ padding: "10px", width: "70%" }}
        />
        <button onClick={sendMessage} style={{ padding: "10px 15px", marginLeft: "10px" }}>
          Send
        </button>
      </div>
    </div>
  );
};

export default ChatPage;
