import React from "react";
import "./PropertyCard.css";

const PropertyCard = ({ property }) => {
  if (!property) return null;

  const {
    "Project Name": projectName,
    BHK,
    Budget,
    "Carpet Area": carpetArea,
    Readiness,
    City,
    Locality,
    Amenities = [],
    Project_ID,
  } = property;

  // Generate slug based on backend logic
  const generateSlug = (name, locality, city, id) => {
    const slugify = (text) =>
      String(text || "")
        .trim()
        .toLowerCase()
        .replace(/\s+/g, "-");

    return `${slugify(name)}-${slugify(locality)}-${slugify(city)}-${id}`;
  };

  const slug = generateSlug(projectName, Locality, City, Project_ID);
  const ctaURL = `/project/${slug}`;

  return (
    <div className="property-card">
      <h3 className="property-title">{projectName}</h3>
      <p className="property-info">
        {City}, {Locality}
      </p>
      <p className="property-info">
        {BHK} | {Budget} | {carpetArea} sq.ft
      </p>
      <p className="property-readiness">
        {Readiness === "READY_TO_MOVE" ? "Ready to Move" : "Under Construction"}
      </p>
      {Amenities.length > 0 && (
        <p className="property-amenities">
          Amenities: {Amenities.slice(0, 3).join(", ")}
        </p>
      )}
      <a href={ctaURL} className="property-cta">
        View Project
      </a>
    </div>
  );
};

export default PropertyCard;
