// src/pages/ChatPage.jsx
import React, { useState } from "react";
import axios from "axios";
import MessageBubble from "../components/MessageBubble";
import PropertyCard from "../components/PropertyCard";
import "./ChatPage.css";

const ChatPage = () => {
  const [messages, setMessages] = useState([
    { sender: "bot", text: "Hi ðŸ‘‹! I can help you find properties. Try: '3BHK flat in Pune under 1Cr'" }
  ]);
  const [input, setInput] = useState("");
  const [loading, setLoading] = useState(false);

  const sendMessage = async () => {
    if (!input.trim()) return;

    // Add user message
    const newMessages = [...messages, { sender: "user", text: input }];
    setMessages(newMessages);
    setInput("");
    setLoading(true);

    try {
      const response = await axios.post("http://localhost:5000/chat", { query: input });
      const data = response.data; // expecting array of { property, summary }

      if (Array.isArray(data)) {
        data.forEach((item) => {
          newMessages.push({ sender: "bot", text: item });
        });
      } else {
        newMessages.push({ sender: "bot", text: { summary: "No results found." } });
      }
      setMessages([...newMessages]);
    } catch (error) {
      console.error(error);
      newMessages.push({ sender: "bot", text: { summary: "Error connecting to backend ðŸ˜¢" } });
      setMessages([...newMessages]);
    } finally {
      setLoading(false);
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === "Enter") {
      sendMessage();
    }
  };

  return (
    <div className="chat-page">
      <div className="chat-window">
        {messages.map((msg, index) => (
          <div key={index} className="chat-message">
            <MessageBubble
              sender={msg.sender}
              text={typeof msg.text === "object" ? msg.text.summary : msg.text}
            />
            {msg.sender === "bot" && msg.text.property && (
              <PropertyCard property={msg.text.property} />
            )}
          </div>
        ))}
        {loading && <div className="loading">Bot is typing...</div>}
      </div>

      <div className="chat-input">
        <input
          type="text"
          placeholder="Type your message..."
          value={input}
          onChange={(e) => setInput(e.target.value)}
          onKeyDown={handleKeyPress}
        />
        <button onClick={sendMessage} disabled={loading}>
          Send
        </button>
      </div>
    </div>
  );
};

export default ChatPage;
