import React, { useEffect, useState } from "react";
import { useParams, Link } from "react-router-dom";
import axios from "axios";
import "./ProjectDetails.css";

const ProjectDetails = () => {
  const { slug } = useParams(); // Get slug from URL
  const [project, setProject] = useState(null);
  const [error, setError] = useState("");

  useEffect(() => {
    const fetchProject = async () => {
      try {
        const res = await axios.get(
          `${process.env.REACT_APP_API_BASE_URL || "http://localhost:5000"}/api/project/${slug}`
        );
        setProject(res.data);
      } catch (err) {
        console.error("Error fetching project:", err);
        setError("Project not found üò¢");
      }
    };

    fetchProject();
  }, [slug]);

  if (error) return <div className="error">{error}</div>;
  if (!project) return <div className="loading">Loading...</div>;

  return (
    <div className="project-details">
      <Link to="/" className="back-btn">‚Üê Back to Search</Link>
      <h2>{project["Project Name"]}</h2>
      <p><b>City:</b> {project.City}</p>
      <p><b>Locality:</b> {project.Locality}</p>
      <p><b>BHK:</b> {project.BHK}</p>
      <p><b>Budget:</b> {project.Budget}</p>
      <p><b>Carpet Area:</b> {project["Carpet Area"]} sq.ft</p>
      <p><b>Readiness:</b> {project.Readiness}</p>

      {project.Amenities?.length > 0 && (
        <p><b>Amenities:</b> {project.Amenities.join(", ")}</p>
      )}

      <p><b>Address:</b> {project.Locality_Details}</p>
    </div>
  );
};

export default ProjectDetails;
