import React, { useState } from "react";
import ChatBox from "../components/ChatBox";
import PropertyCard from "../components/PropertyCard";
import axios from "axios";

const ChatPage = () => {
  const [messages, setMessages] = useState([
    {
      sender: "bot",
      text: "Hi üëã! I can help you find properties. Try: '3BHK flat in Pune under 1Cr'",
    },
  ]);
  const [input, setInput] = useState("");
  const [properties, setProperties] = useState([]);

  const sendMessage = async () => {
    if (!input.trim()) return;

    const newMessages = [...messages, { sender: "user", text: input }];
    setMessages(newMessages);
    setInput("");
    setProperties([]);

    try {
      const res = await axios.post(
        `${process.env.REACT_APP_API_BASE_URL || "http://localhost:5000"}/api/chat`,
        { query: input }
      );

      const data = res.data;

      if (!Array.isArray(data) || data.length === 0) {
        setMessages([
          ...newMessages,
          { sender: "bot", text: "No matching properties found." },
        ]);
        return;
      }

      const botReply = `Found ${data.length} matching project(s) üëá`;
      setMessages([...newMessages, { sender: "bot", text: botReply }]);
      setProperties(data.map((d) => d.property)); // store property details
    } catch (err) {
      console.error("‚ùå Backend error:", err);
      setMessages([
        ...newMessages,
        { sender: "bot", text: "Error connecting to backend üò¢" },
      ]);
    }
  };

  return (
    <div style={{ maxWidth: "850px", margin: "0 auto", padding: "20px" }}>
      <ChatBox messages={messages} />

      {/* Input Section */}
      <div style={{ display: "flex", marginTop: "15px" }}>
        <input
          type="text"
          value={input}
          onChange={(e) => setInput(e.target.value)}
          placeholder="Type your query..."
          style={{
            flex: 1,
            padding: "10px",
            borderRadius: "6px",
            border: "1px solid #ccc",
          }}
          onKeyPress={(e) => e.key === "Enter" && sendMessage()}
        />
        <button
          onClick={sendMessage}
          style={{
            marginLeft: "10px",
            padding: "10px 18px",
            backgroundColor: "#007bff",
            color: "white",
            border: "none",
            borderRadius: "6px",
            cursor: "pointer",
          }}
        >
          Send
        </button>
      </div>

      {/* Property Results */}
      {properties.length > 0 && (
        <div style={{ marginTop: "30px" }}>
          {properties.map((p, idx) => (
            <PropertyCard key={idx} property={p} />
          ))}
        </div>
      )}
    </div>
  );
};

export default ChatPage;
