import React from "react";
import "./PropertyCard.css";

const PropertyCard = ({ property }) => {
  if (!property) return null;

  const {
    "Project Name": projectName,
    BHK,
    Budget,
    Readiness,
    City,
    Locality,
    Locality_Details,
    Project_ID,
  } = property;

  const slug = projectName?.toLowerCase().replace(/\s+/g, "-") || "project";
  const projectUrl = `/project/${slug}`;

  // Dummy amenities (you can add real ones later)
  const amenities = ["Gym", "Clubhouse", "Swimming Pool"];

  return (
    <div className="property-card">
      <div className="property-header">
        <h3>{projectName}</h3>
        <span className="price">{Budget}</span>
      </div>
      <p>
        <strong>{BHK}</strong> ‚Ä¢ {Readiness.replace("_", " ")}
      </p>
      <p>
        üìç {Locality}, {City}
      </p>
      <p className="details">{Locality_Details}</p>

      <div className="amenities">
        {amenities.slice(0, 3).map((a, i) => (
          <span key={i} className="amenity">
            {a}
          </span>
        ))}
      </div>

      <a href={projectUrl} className="cta-btn">
        View Details
      </a>
    </div>
  );
};

export default PropertyCard;
